import React from 'react'
import styled from 'styled-components'

import { CustomButton } from './CustomButton'

const Container = styled.div`
  width: 22%;
  display: flex;
  flex-direction: column;
  height: 350px;
  align-items: center;
  position: relative;

  &.custom-button {
    width: 80%;
    opacity: 0.7;
    position: absolute;
    top: 255px;
    display: none;
  }

  :hover {
    img {
      opacity: 0.8;
    }

    button {
      opacity: 0.85;
      display: flex;
    }
  }
`

const Image = styled.img`
  width: 100%;
  height: 95%;
  background-size: cover;
  background-position: center;
  margin-bottom: 5px;
  background-image: url(${props => props.image});
`

const Footer = styled.div`
  width: 100%;
  height: 5%;
  display: flex;
  justify-content: space-between;
  font-size: 18px;
`

const Name = styled.span`
  width: 90%;
  margin-bottom: 15px;
  margin-right: 50%;
`

const Price = styled.span`
  width: 10%;
`

const Button = styled.button`
  width: 50%;
  opacity: 0.7;
  position: absolute;
  top: 255px;
  display: none;
`

export const CollectionItem = ({ id, name, price, imageUrl }) => (
  <Container>
    <Image image={imageUrl} />
    <Footer>
      <Name>{name}</Name>
      <Price>{price}</Price>
    </Footer>
    <CustomButton className={'custom-button'} inverted>Add to cart</CustomButton>
    {/* <Button>

    </Button> */}
  </Container>
)
