import { ShopActionTypes } from './shop.types'

const INITIAL_STATE = {
  collections: null,
  isFetching: false
}

export const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case ShopActionTypes.FETCH_COLLECTIONS_START:
      return
    case ShopActionTypes.UPDATE_COLLECTIONS:
    return {
      ...state,
      collections: action.payload
    }
    default:
      return state
  }
}
