import StripeCheckout from 'react-stripe-checkout'

export const StripeCheckoutButton = ({ price }) => {
  const priceForStripe = price * 100
  const publishableKey = 'pk_test_51HEwahKGyWO5ehPzZiFa0nrzo4bOlYMR91wOyyI6Mdommlaf6vJeJPnoYc2rHSXSc853PcXShwiFxZCR5PfGNri80013NZ7lwW'
  const onToken = token => {
    console.log(token)
    alert('Payment Successful')
  }

  return (
    <StripeCheckout
    label='Pay Now'
    name='Clothing Store'
    billingAddress
    shippingAddress
    image='https://sendeyo.com/up/d/f3eb2117da'
    description={`Your total is $${price}`}
    amount={priceForStripe}
    panelLabel='Pay Now'
    token={onToken}
    stripeKey={publishableKey}
    />
  )
}
